import "./reserveTutorscr.css"

import React, { useState, useEffect } from 'react';

function MyComponent() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data));
  }, []);

  const handleSelectChange = event => {
    setSelectedUser(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();
    const data = { selectedUser };
    fetch('https://example.com/api', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(response => response.json())
    .then(data => {
      console.log('Success:', data);
      // do something with the response data, such as display a success message
    })
    .catch((error) => {
      console.error('Error:', error);
      // handle error
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="user-select">Choose a user:</label>
        <select id="user-select" value={selectedUser} onChange={handleSelectChange}>
          <option value="">Select a user</option>
          {users.map(user => (
            <option key={user.id} value={user.id}>
              {user.name}
            </option>
          ))}
        </select>
        <button type="submit">Submit</button>
      </form>
      {selectedUser && users.find(user => user.id === selectedUser) && (
        <p>You selected {users.find(user => user.id === selectedUser).name}</p>
      )}
    </div>
  );
}

export default MyComponent;
