import React, { useState } from "react";
import "./Carousel.css";

const images = [
  {
    id: 1,
    url:
      "https://www.msdelta.edu/ghec/images/tutoring.png",
    alt: "Image 1",
    caption: "This is the caption for Image 1",
  },
  {
    id: 2,
    url:
      "data:image/jpeg;base64,",
    alt: "Image 2",
    caption: "This is the caption for Image 2",
  }
];

const Carousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const handlePrevClick = () => {
    const index = currentIndex === 0 ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(index);
  };

  const handleNextClick = () => {
    const index = currentIndex === images.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(index);
  };

  return (
    <div className="carousel">
      <button className="carousel__button" onClick={handlePrevClick}>
        Prev
      </button>
      <img
        className="carousel__image"
        src={images[currentIndex].url}
        alt={images[currentIndex].alt}
      />
      <div className="carousel__caption">{images[currentIndex].caption}</div>
      <button className="carousel__button" onClick={handleNextClick}>
        Next
      </button>
    </div>
  );
};

export default Carousel;
