import "./reserveTutorscr.css"
import React, { useState, useEffect } from 'react';

function TutorDropdown() {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState('');

  useEffect(() => {
    fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/tutors')
      .then(response => response.json())
      .then(data => setTutors(data))
      .catch(error => console.log(error));
  }, []);

  const handleSelect = (event) => {
    setSelectedTutor(event.target.value);
  }

  return (
    <div>
      <h1>Reserve a tutor!</h1>
      <label htmlFor="tutors">Select a tutor:</label>
      <select id="tutors" value={selectedTutor} onChange={handleSelect}>
        <option value="">Choose a tutor</option>
        {Array.isArray(tutors) && tutors.map((tutor, index) => (
          <option key={index} value={tutor.name}>{tutor.name}</option>
        ))}
      </select>
      {selectedTutor && <p>You selected {selectedTutor}.</p>}
    </div>
  );
}

export default TutorDropdown;
