import "./reserveTutorscr.css"

import React, { useState } from 'react';
import axios from 'axios';

const ReserveTutorPage = () => {

  const [id, setID] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [tutor, setTutor] = useState('');
  const [subject, setSubject] = useState('');


  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/reservations', {
        id,
        date,
        time,
        tutor,
        subject,
      });
      console.log(response.data); // handle the response data as per your requirement
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        ID:
        <input type="text" value={id} onChange={(event) => setID(event.target.value)} required />
      </label>
      <label>
        Date:
        <input type="date" value={date} onChange={(event) => setDate(event.target.value)} required />
      </label>
      <label>
        Time:
        <input type="time" value={time} onChange={(event) => setTime(event.target.value)} required />
      </label>
      <label>
        Tutor:
        <input type="text" value={tutor} onChange={(event) => setTutor(event.target.value)} required />
      </label>
      <label>
        Subject:
        <input type="text" value={subject} onChange={(event) => setSubject(event.target.value)} required />
      </label>
      <button type="submit">Reserve</button>
    </form>
  );
};

export default ReserveTutorPage;
