import "./reserveTutorscr.css"


import { useState, useEffect } from "react";
import axios from "axios";

function ReserveTutorPage() {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState("");
  const [studentName, setStudentName] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [selectedSubject, setSelectedSubject] = useState("Math"); // set default subject to Math

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/reservations");
      setTutors(response.data.body.filter(tutor => tutor.subject === selectedSubject));
    };
    fetchData();
  }, [selectedSubject]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Send reservation data to the API
    console.log("Submitting reservation: ", selectedTutor, studentName, date, time);
  };

  return (
    <div className="container">
      <h1>Reserve a Tutor for {selectedSubject}</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Select a Tutor:
          <select value={selectedTutor} onChange={(e) => setSelectedTutor(e.target.value)}>
            <option value="">Select a tutor</option>
            {tutors.map((tutor) => (
              <option value={tutor.id} key={tutor.id}>
                {tutor.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Student Name:
          <input type="text" value={studentName} onChange={(e) => setStudentName(e.target.value)} />
        </label>
        <label>
          Date:
          <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
        </label>
        <label>
          Time:
          <input type="time" value={time} onChange={(e) => setTime(e.target.value)} />
        </label>
        <label>
          Select a Subject:
          <select value={selectedSubject} onChange={(e) => setSelectedSubject(e.target.value)}>
            <option value="Math">Math</option>
            <option value="Science">Science</option>
            <option value="English">English</option>
          </select>
        </label>
        <button type="submit">Reserve</button>
      </form>
    </div>
  );
}

export default ReserveTutorPage;
