import React from 'react';
import { Carousel } from 'react-bootstrap';
import "./HeroImgStyles.css";

import IntroImg from "../assets/introimg_2.webp";

import Img2 from "../assets/books.jpeg";
import { Link } from "react-router-dom";

const HeroImg = () => {
  return (
    <div className="hero">
      <Carousel>
        <Carousel.Item>
          <img className="d-block w-100 intro-img" src={IntroImg} alt="IntroImg" />
        </Carousel.Item>
        <Carousel.Item>
          <img className="d-block w-100" src={Img2} alt="Third slide" />
        </Carousel.Item>
      </Carousel>
      <div className="content">
        <p>Welcome to Tutoring Academy</p>
        <h1>Reserve a tutor now.</h1>
        <div>
          <Link to="/reservetutor" className="btn btn-light">
            Book Now
          </Link>
        </div>
      </div>
    </div>
  );
};

export default HeroImg;
