import React, { useState } from 'react';
import DateTime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';

function TutorSelection() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [availableTimes, setAvailableTimes] = useState([]);
  const [selectedTime, setSelectedTime] = useState(null);

  function handleDateChange(moment) {
    setSelectedDate(moment.toDate());

    // hard-coded list of available times for the selected tutor and date
    const tutor = {
      name: 'John Smith',
      availability: {
        '2023-05-05': ['10:00 AM', '11:00 AM', '12:00 PM'],
        '2023-05-06': ['2:00 PM', '3:00 PM', '4:00 PM'],
        '2023-05-07': ['11:00 AM', '12:00 PM', '1:00 PM'],
      },
    };

    const date = moment.format('YYYY-MM-DD');
    const times = tutor.availability[date] || [];

    setAvailableTimes(times);
    setSelectedTime(null);
  }

  function handleTimeChange(event) {
    setSelectedTime(event.target.value);
  }

  function handleSubmit(event) {
    event.preventDefault();

    // handle form submission with selected date and time
    console.log('Selected date:', selectedDate);
    console.log('Selected time:', selectedTime);
  }

  return (
    <div>
      <h1>Select a Tutor and Date</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tutor">Tutor:</label>
        <select id="tutor" disabled={availableTimes.length === 0}>
          <option value={null}>Select a tutor</option>
          <option value="John Smith">{tutor.name}</option>
        </select>
        <br />
        <label htmlFor="date">Date:</label>
        <DateTime id="date" input={false} onChange={handleDateChange} />
        <br />
        <label htmlFor="time">Time:</label>
        <select id="time" value={selectedTime} onChange={handleTimeChange} disabled={availableTimes.length === 0}>
          <option value={null}>Select a time</option>
          {availableTimes.map(time => (
            <option key={time} value={time}>
              {time}
            </option>
          ))}
        </select>
        <br />
        <button type="submit" disabled={!selectedDate || !selectedTime}>
          Submit
        </button>
      </form>
    </div>
  );
}
