import "./reserveTutorscr.css"

import { useState } from 'react';

const tutors = [
  {id: 1, 
    name: 'Martin Smith', 
    availabilities: [
      { date: '2023-05-10', times: ['10:00', '11:00', '12:00'] },
      { date: '2023-05-12', times: ['14:00', '15:00', '16:00'] },
      { date: '2023-05-14', times: ['13:00', '14:00', '15:00'] },
    ],
  },
  { id: 2, name: 'Test9 3' },
  { id: 3, name: 'wass Doe' },
  { id: 4, name: 'Test229 3' },
  { id: 5, name: 'Layla Arwa' },
  { id: 6, name: 'test222 Doe' },
  { id: 7, name: 'Test9 3' },
  { id: 8, name: 'Test2129 3' },
  { id: 9, name: 'Test29 3' },
  { id: 10, name: '1wass Doe' },
  { id: 11, name: 'test222 Doe' },
  { id: 12, name: 'test222 Doe' },
  { id: 13, name: 'Test5 3' },
  { id: 14, name: 'Test9 3' },
  { id: 15, name: 'Test29 3' },
  { id: 16, name: 'Test5 3' },
  { id: 17, name: 'Test29 3' },
  { id: 18, name: 'Test9 3' },
  { id: 19, name: 'Test929 3' },
  { id: 20, name: 'Test229 3' },
  { id: 21, name: '2wass Doe' },
  { id: 22, name: 'Test 3' },
  { id: 23, name: 'Test5 3' },
  { id: 24, name: 'Test9 3' },
  { id: 25, name: 'John Doe' },
  { id: 26, name: 'Test29 3' },
  { id: 27, name: 'Test2129 3' },
  { id: 28, name: 'test222 Doe' },
  { id: 29, name: 'Test 3' },
  { id: 30, name: 'Test19 3' },
  { id: 31, name: 'Test29 3' },
  { id: 32, name: 'Test5 3' },
  { id: 33, name: 'Test29 3' },
  { id: 34, name: 'Test 2' },
  { id: 35, name: 'John White' },
  { id: 36, name: 'Test29 3' },
  { id: 37, name: 'Test929 3' },

];

const subjects = [
  { id: 1, name: 'Integral Calculus' },
  { id: 2, name: 'Linear Algebra' }
];

const apiUrl = 'https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations';

function ReserveTutorPage() {
  const [tutorId, setTutorId] = useState(null);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [duration, setDuration] = useState('');
  const [subjectId, setSubjectId] = useState(null);

  // Helper function to get the available times for a selected tutor and date
  function getAvailableTimes(tutor, selectedDate) {
    const availability = tutor.availabilities.find(item => item.date === selectedDate);
    return availability ? availability.times : [];
  }

  function handleSubmit(event) {
    event.preventDefault();
    const reservation = {
      tutor_id: tutorId,
      student_id: 2, 
      date,
      subject_id: subjectId,
      time,
      duration,
      status: 'Incomplete',
    };
    fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(reservation),
    })
      .then(response => {
        if (response.ok) {
          alert('Reservation created successfully!');
          setTutorId(null);
          setDate('');
          setTime('');
          setDuration('');
          setSubjectId(null);
        } else {
          throw new Error('Failed to create reservation');
        }
      })
      .catch(error => {
        console.error(error);
        alert('Failed to create reservation');
      });
  }

  return (
    <form onSubmit={handleSubmit}>
      <h1>Reserve a tutor now:</h1>
      <label>
        Tutor:
        <select value={tutorId} onChange={event => setTutorId(parseInt(event.target.value))}>
          <option value="">-- Choose a tutor --</option>
          {tutors.map(tutor => (
            <option key={tutor.id} value={tutor.id}>
              {tutor.name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Subject:
        <select value={subjectId} onChange={event => setSubjectId(parseInt(event.target.value))}>
          <option value="">-- Choose a subject --</option>
          {subjects.map(subject => (
            <option key={subject.id} value={subject.id}>
              {subject.name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
  Date:
  <input type="date" value={date} onChange={event => setDate(event.target.value)} min="1000-01-01" max="9999-12-31" />
</label>
      <br />
      <label>
        Time:
        <input type="time" value={time} onChange={event => setTime(event.target.value)} />
      </label>
      <br />
      <label>
        Duration (in hours):
        <input type="number" min="1" step="0.5" value={duration} onChange={event => setDuration(event.target.value)} />
      </label>
      <br />
      <button type="submit" disabled={!tutorId || !subjectId || !date || !time || !duration}>
        Submit
      </button>
    </form>
  );
}
export default ReserveTutorPage;
