import React, { useState, useEffect } from 'react';

function MyComponent() {
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState('');
  const [selectedTutor, setSelectedTutor] = useState('');

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setUsers(data));
  }, []);

  const handleUserSelectChange = event => {
    setSelectedUser(event.target.value);
  };

  const handleTutorSelectChange = event => {
    setSelectedTutor(event.target.value);
  };

  const handleSubmit = event => {
    event.preventDefault();
    const data = {
      user: selectedUser,
      tutor: selectedTutor
    };
    fetch('https://example.com/api/book-tutor', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Success:', data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="user-select">Choose a user:</label>
          <select id="user-select" value={selectedUser} onChange={handleUserSelectChange}>
            <option value="">Select a user</option>
            {users.map(user => (
              <option key={user.id} value={user.id}>
                {user.name}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="tutor-select">Choose a tutor:</label>
          <select id="tutor-select" value={selectedTutor} onChange={handleTutorSelectChange}>
            <option value="">Select a tutor</option>
            <option value="1">Tutor A</option>
            <option value="2">Tutor B</option>
            <option value="3">Tutor C</option>
          </select>
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default MyComponent;
