import "./reserveTutorscr.css"

const App = () => {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState(null);
  const [reservation, setReservation] = useState(null);

  useEffect(() => {
    fetchTutors();
  }, []);

  const fetchTutors = async () => {
    try {
      const response = await axios.get(
        "https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/tutors"
      );
      const apiTutors = response.data.body.map((tutor) => ({
        id: tutor.tutor_id,
        name: `${tutor.first_name} ${tutor.last_name}`,
        subject: tutor.subject,
        grade: tutor.grade,
        status: 'Incomplete',
      }));
      setTutors(apiTutors);
    } catch (error) {
      console.error("Error fetching tutors:", error);
    }
  };

  const handleTutorClick = (tutor) => {
    setSelectedTutor(tutor);
  };

  const handleReservationSubmit = async (event) => {
    event.preventDefault();
    const reservationData = {
      tutor_id: selectedTutor.id,
      student_id: 2,
      date: "2023-04-31",
      subject_id: 1,
      time: "1:00",
      duration: "1",
      status: "Incomplete",
    };
    try {
      const response = await axios.post(
        "https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations",
        reservationData
      );
      console.log(response.data);
      setReservation(response.data.body);
    } catch (error) {
      console.error("Error creating reservation:", error);
    }
  };

  return (
    <div className="container">
      <h1>Reserve a Tutor</h1>
      <div className="tutor-list">
        <h2>Tutors</h2>
        <ul>
          {tutors.map((tutor) => (
            <li key={tutor.id} onClick={() => handleTutorClick(tutor)}>
              {tutor.name} - {tutor.subject} - {tutor.grade}
            </li>
          ))}
        </ul>
      </div>
      <div className="reservation-form">
        <h2>Reservation Form</h2>
        {selectedTutor && (
          <form onSubmit={handleReservationSubmit}>
            <label>
              Date:
              <input type="date" name="date" required />
            </label>
            <label>
              Time:
              <input type="time" name="time" required />
            </label>
            <label>
              Duration:
              <select name="duration" required>
                <option value="1">1 hour</option>
                <option value="2">2 hours</option>
                <option value="3">3 hours</option>
              </select>
            </label>
            <button type="submit">Submit Reservation</button>
          </form>
        )}
      </div>
      <div className="reservation-status">
        {reservation && (
          <p>
            Your reservation for {selectedTutor.name} on {reservation.date} at{" "}
            {reservation.time} for {reservation.duration} hour(s) has been
            created!
          </p>
        )}
      </div>
    </div>
  );
};

export default App;
