import "./reserveTutorscr.css"

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ReserveTutor = () => {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTime, setSelectedTime] = useState(null);

  useEffect(() => {
    const fetchTutors = async () => {
      const response = await axios.get('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/reservations');
      setTutors(response.data.body);
    };

    fetchTutors();
  }, []);

  const handleTutorSelect = (tutor) => {
    setSelectedTutor(tutor);
  };

  const handleDateSelect = (date) => {
    setSelectedDate(date);
  };

  const handleTimeSelect = (time) => {
    setSelectedTime(time);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Make the reservation with the selected tutor, date, and time
    console.log(`Tutor: ${selectedTutor.tutor_first_name} ${selectedTutor.tutor_last_name}`);
    console.log(`Subject: ${selectedTutor.subject}`);
    console.log(`Date: ${selectedDate.date}`);
    console.log(`Time: ${selectedTime.time}`);
  };

  return (
    <div>
      <h1>Reserve a Tutor</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="tutorSelect">Select a tutor:</label>
        <select id="tutorSelect" onChange={(event) => handleTutorSelect(JSON.parse(event.target.value))}>
          <option value="">--Please select a tutor--</option>
          {tutors.map((tutor, index) => (
            <option key={index} value={JSON.stringify(tutor)}>
              {tutor.tutor_first_name} {tutor.tutor_last_name} ({tutor.subject})
            </option>
          ))}
        </select>
        <br />

        <label htmlFor="dateSelect">Select a date:</label>
        <input type="date" id="dateSelect" onChange={(event) => handleDateSelect(event.target.value)} />
        <br />

        <label htmlFor="timeSelect">Select a time:</label>
        <input type="time" id="timeSelect" onChange={(event) => handleTimeSelect(event.target.value)} />
        <br />

        <button type="submit" disabled={!selectedTutor || !selectedDate || !selectedTime}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default ReserveTutor;
