import "./reserveTutorscr.css"

import React, { useState } from 'react';
import axios from 'axios';

const ReserveTutorPage = () => {

  const [id, setID] = useState('');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/reservations', {
        name,
        email,
        date,
        time,
      });
      console.log(response.data); // handle the response data as per your requirement
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={(event) => setName(event.target.value)} required />
      </label>
      <label>
        Email:
        <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} required />
      </label>
      <label>
        Date:
        <input type="date" value={date} onChange={(event) => setDate(event.target.value)} required />
      </label>
      <label>
        Time:
        <input type="time" value={time} onChange={(event) => setTime(event.target.value)} required />
      </label>
      <button type="submit">Reserve</button>
    </form>
  );
};

export default ReserveTutorPage;
