import React, { useState, useEffect } from 'react';
import "./reserveTutorscr.css";

function ReserveTutorPage() {
  const [formData, setFormData] = useState({
    id: tutor.tutor_id,
        name: `${tutor.first_name} ${tutor.last_name}`,
        subject: tutor.subject,
        grade: tutor.grade,
    status: 'Incomplete'
  });

  const [errorMessage, setErrorMessage] = useState('');
  const [tutors, setTutors] = useState([]);

  useEffect(() => {
    fetchTutors();
  }, []);

  const fetchTutors = async () => {
    try {
      const response = await fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/tutors');
      const data = await response.json();
      setTutors(data);
    } catch (error) {
      console.error('Error fetching tutors:', error);
    }
  };

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value
    }));
  }

  function handleSubmit(event) {
    event.preventDefault();
    // Send reservation data to API endpoint
    fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations', {
      method: 'POST',
      mode: 'no-cors',
      body: JSON.stringify({ body: formData }),
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Failed to create reservation');
        }
        // Reservation created successfully
        alert('Reservation created!');
      })
      .catch(error => {
        setErrorMessage(error.message);
      });
  }
return (
    <div>
   <div className="tutors-list">
        <h2>List of Tutors</h2>
        {tutors.map((tutor, index) => (
          <div key={index} className="tutor-item">
            <p>
              <strong>ID:</strong> {tutor.tutor_id}
            </p>
            <p>
              <strong>Name:</strong> {tutor.first_name} {tutor.last_name}
            </p>
            <p>
              <strong>Main Subject:</strong> {tutor.mainsubject}
            </p>
            <p>
              <strong>Grade:</strong> {tutor.grade}
            </p>
            <p>
              <strong>Subject IDs:</strong> {tutor.subjectids.join(', ')}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
export default ReserveTutorPage;
