import React, { useState } from 'react';
import DateTime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';

function ReserveTutorPage() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [availableTutors, setAvailableTutors] = useState([]);

  function handleDateChange(moment) {
    setSelectedDate(moment.toDate());

    // hard-coded list of available tutors
    const tutors = [
      { id: 1, name: 'John Smith' },
      { id: 2, name: 'Jane Doe' },
      { id: 3, name: 'Bob Johnson' }
    ];

    // filter tutors based on availability
    const filteredTutors = tutors.filter(tutor => {
      // check if tutor is available at selected date and time
      // (this is just an example, you would need to replace this with your own logic)
      const isAvailable = Math.random() < 0.5;
      return isAvailable;
    });

    setAvailableTutors(filteredTutors);
  }

  return (
    <div>
      <h1>Select a Date and Time</h1>
      <DateTime onChange={handleDateChange} />
      {availableTutors.length > 0 && (
        <div>
          <h2>Select a Tutor</h2>
          <ul>
            {availableTutors.map(tutor => (
              <li key={tutor.id}>{tutor.name}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
export default ReserveTutorPage
