import "./reserveTutorscr.css"
import React, { useState } from 'react';
import DateTime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';

function ReserveTutorPage() {
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedTutor, setSelectedTutor] = useState(null);
  const [availableTutors, setAvailableTutors] = useState([]);

  function handleDateChange(moment) {
    setSelectedDate(moment.toDate());

    // hard-coded list of available tutors and their availability
    const tutors = [
      {
        id: 1,
        name: 'John Smith',
        availability: {
          Monday: ['10:00 AM', '11:00 AM', '12:00 PM'],
          Tuesday: ['2:00 PM', '3:00 PM', '4:00 PM'],
          Wednesday: ['11:00 AM', '12:00 PM', '1:00 PM'],
        },
      },
      {
        id: 2,
        name: 'Jane Doe',
        availability: {
          Monday: ['1:00 PM', '2:00 PM', '3:00 PM'],
          Wednesday: ['9:00 AM', '10:00 AM', '11:00 AM'],
          Friday: ['3:00 PM', '4:00 PM', '5:00 PM'],
        },
      },
      {
        id: 3,
        name: 'Bob Johnson',
        availability: {
          Tuesday: ['10:00 AM', '11:00 AM', '12:00 PM'],
          Thursday: ['2:00 PM', '3:00 PM', '4:00 PM'],
          Friday: ['10:00 AM', '11:00 AM', '12:00 PM'],
        },
      },
    ];

    // filter tutors based on availability for selected date and time
    const filteredTutors = tutors.filter(tutor => {
      const day = selectedDate.toLocaleDateString('en-US', { weekday: 'long' });
      const time = selectedDate.toLocaleTimeString('en-US', { hour: 'numeric', minute: 'numeric' });
      return tutor.availability[day]?.includes(time);
    });

    setAvailableTutors(filteredTutors);
    setSelectedTutor(null); // reset selected tutor when date/time changes
  }

  function handleTutorChange(event) {
    const tutorId = parseInt(event.target.value);
    setSelectedTutor(tutorId);
  }

  return (
    <div>
      <h1>Select a Date and Time</h1>
      <DateTime onChange={handleDateChange} />
      {availableTutors.length > 0 && (
        <div>
          <h2>Select a Tutor</h2>
          <select value={selectedTutor} onChange={handleTutorChange}>
            <option value={null}>Select a tutor</option>
            {availableTutors.map(tutor => (
              <option key={tutor.id} value={tutor.id}>
                {tutor.name}
              </option>
            ))}
          </select>
        </div>
      )}
    </div>
  );
}
export default ReserveTutorPage;
