import "./reserveTutorscr.css"

import React, { useState } from "react";

const ReserveTutorPage = () => {
  const [tutorId, setTutorId] = useState("");
  const [studentId, setStudentId] = useState("");
  const [date, setDate] = useState("");
  const [subjectId, setSubjectId] = useState("");
  const [time, setTime] = useState("");
  const [duration, setDuration] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    const reservationData = {
      tutor_id: tutorId,
      student_id: studentId,
      date: date,
      subject_id: subjectId,
      time: time,
      duration: duration,
      status: "Incomplete"
    };

    fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations', {
      method: 'POST',
      body: JSON.stringify({ body: reservationData })
    })
    .then(response => response.json())
    .then(data => {
      console.log(data);
      // Handle successful reservation here
    })
    .catch(error => {
      console.error(error);
      // Handle error here
    });
  };

  return (
    <div>
      <h2>Make a Tutor Reservation</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Tutor ID:</label>
          <input type="text" value={tutorId} onChange={(e) => setTutorId(e.target.value)} />
        </div>
        <div>
          <label>Student ID:</label>
          <input type="text" value={studentId} onChange={(e) => setStudentId(e.target.value)} />
        </div>
        <div>
          <label>Date:</label>
          <input type="text" value={date} onChange={(e) => setDate(e.target.value)} />
        </div>
        <div>
          <label>Subject ID:</label>
          <input type="text" value={subjectId} onChange={(e) => setSubjectId(e.target.value)} />
        </div>
        <div>
          <label>Time:</label>
          <input type="text" value={time} onChange={(e) => setTime(e.target.value)} />
        </div>
        <div>
          <label>Duration:</label>
          <input type="text" value={duration} onChange={(e) => setDuration(e.target.value)} />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ReserveTutorPage;
