import "./reserveTutorscr.css"

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TutorReservationPage = () => {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState('');
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [time, setTime] = useState('');
  const [duration, setDuration] = useState('');

  useEffect(() => {
    const fetchTutors = async () => {
      const response = await axios.get('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/tutors');
      setTutors(response.data);
    };
    fetchTutors();
  }, []);

  const handleSelectTutor = (event) => {
    setSelectedTutor(event.target.value);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleSelectSubject = (event) => {
    setSelectedSubject(event.target.value);
  };

  const handleTimeChange = (event) => {
    setTime(event.target.value);
  };

  const handleDurationChange = (event) => {
    setDuration(event.target.value);
  };

  const handleReserve = async () => {
    const data = {
      tutor_id: selectedTutor,
      student_id: name,
      date,
      subject_id: selectedSubject,
      time,
      duration,
      status: 'Incomplete'
    };
    const response = await axios.post('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations', data);
    console.log(response.data);
  };

  return (
    <div>
      <h1>Tutor Reservation Page</h1>
      <label>
        Select a tutor:
        <select value={selectedTutor} onChange={handleSelectTutor}>
          {tutors.map(tutor => (
            <option key={tutor.id} value={tutor.id}>
              {tutor.name}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Name:
        <input type="text" value={name} onChange={handleNameChange} />
      </label>
      <br />
      <label>
        Email:
        <input type="email" value={email} onChange={handleEmailChange} />
      </label>
      <br />
      <label>
        Date:
        <input type="date" value={date} onChange={handleDateChange} />
      </label>
      <br />
      <label>
        Select a subject:
        <select value={selectedSubject} onChange={handleSelectSubject}>
          <option value="math">Math</option>
          <option value="english">English</option>
          <option value="history">History</option>
        </select>
      </label>
      <br />
      <label>
        Time:
        <input type="time" value={time} onChange={handleTimeChange} />
      </label>
      <br />
      <label>
        Duration:
        <input type="number" value={duration} onChange={handleDurationChange} />
      </label>
      <br />
      </div>

  )
          }

  export default TutorReservationPage;
