import React, { useState, useMemo } from "react";
import Navbar from "../components/Navbar";
import AppointmentCardList from "../components/AppointmentCardList";
import Footer from "../components/Footer";
import Layout from "../components/Layout";
import axios from "axios";

const Schedule = () => {
  return (
    <div>
      <Navbar />
      <MySchedule />
      <Footer />
    </div>
  );
};

function MySchedule() {
  const [searchTerm, setSearchTerm] = useState("");
  const [sorted, setSorted] = useState(false);

  const [data, setTutors] = useState([]);

  useMemo(() => {
    const retrieveData = async () => {
      const proxyUrl = "http://localhost:8080/";
      const apiUrl =
        "https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/reservations";
      const resp = await axios.get(proxyUrl + apiUrl);
      console.log("Response: " + JSON.stringify(resp.data));
      setTutors(resp.data);
    };

    retrieveData();
  }, []);

  function handleSearchTerm(event) {
    setSearchTerm(event.target.value);
  }

  // ... handleSortByFirstName, handleSortByLastName, and handleSortBySubject functions remain unchanged.

  const filteredTutors = data.filter((tutor) =>
    tutor.tutor_first_name
      .toLowerCase()
      .startsWith(searchTerm.toLowerCase()) ||
    tutor.tutor_last_name
      .toLowerCase()
      .startsWith(searchTerm.toLowerCase()) ||
    tutor.tutor_first_name
      .concat(" ")
      .concat(tutor.tutor_last_name)
      .toLowerCase()
      .startsWith(searchTerm.toLowerCase()) ||
    tutor.subject.toLowerCase().startsWith(searchTerm.toLowerCase())
  );

  console.log("Filtered Tutors:", filteredTutors);
  return (
    <div>
      <Layout />
      <AppointmentCardList data={filteredTutors} />
      <Layout />
    </div>
  );
}

export default Schedule;
