import "./reserveTutorscr.css"

import React, { useState, useEffect } from 'react';

function ReserveTutorPage() {
  const [tutors, setTutors] = useState([]);
  const [selectedTutor, setSelectedTutor] = useState(null);
  const [reservation, setReservation] = useState({
    tutor_id: null,
    student_id: 2, // Assuming the student ID is fixed
    date: '',
    subject_id: 1, // Assuming the subject ID is fixed
    time: '',
    duration: '',
    status: 'Incomplete',
  });
  const [reservationResponse, setReservationResponse] = useState(null);

  useEffect(() => {
    fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/show/tutors')
      .then(response => response.json())
      .then(data => setTutors(data))
      .catch(error => console.error(error));
  }, []);

  const handleTutorSelect = tutor => {
    setSelectedTutor(tutor);
    setReservation({ ...reservation, tutor_id: tutor.id });
  };

  const handleInputChange = event => {
    const { name, value } = event.target;
    setReservation({ ...reservation, [name]: value });
  };

  const handleReservationSubmit = event => {
    event.preventDefault();
    fetch('https://x4g0ddpp1f.execute-api.us-east-2.amazonaws.com/prod/Create/reservations', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(reservation),
    })
      .then(response => response.json())
      .then(data => setReservationResponse(data))
      .catch(error => console.error(error));
  };

  return (
    <div>
      <h1>Reserve a Tutor</h1>
      <h2>Select a Tutor</h2>
      <ul>
        {tutors.map(tutor => (
          <li key={tutor.id} onClick={() => handleTutorSelect(tutor)}>
            {tutor.name}
          </li>
        ))}
      </ul>
      {selectedTutor && (
        <form onSubmit={handleReservationSubmit}>
          <h2>Reservation Details</h2>
          <label>
            Date:
            <input type="date" name="date" value={reservation.date} onChange={handleInputChange} />
          </label>
          <br />
          <label>
            Time:
            <input type="time" name="time" value={reservation.time} onChange={handleInputChange} />
          </label>
          <br />
          <label>
            Duration (in hours):
            <input type="number" name="duration" value={reservation.duration} onChange={handleInputChange} />
          </label>
          <br />
          <button type="submit">Reserve Tutor</button>
        </form>
      )}
      {reservationResponse && (
        <div>
          {reservationResponse.success ? (
            <p>Reservation successful!</p>
          ) : (
            <p>Reservation failed: {reservationResponse.message}</p>
          )}
        </div>
      )}
    </div>
  );
}

export default ReserveTutorPage;
